function base64ToBytes(base64) {
  const binString = atob(base64);
  return Uint8Array.from(binString, (m) => m.codePointAt(0));
}

function bytesToBase64(bytes) {
  const binString = Array.from(bytes, (byte) =>
    String.fromCodePoint(byte),
  ).join("");
  return btoa(binString);
}

function parseTimecode(timecode) {
    var a = timecode.split(":");
    var h = parseInt(a[0]);
    var m = parseInt(a[1]);
    var s = parseInt(a[2].split(".")[0]);
    var ms = parseFloat("0." + a[2].split(".")[1]);
    return h * 60 * 60 + m * 60 + s + ms;
}

function seek(seconds) {
    window.player.videoContainer.streamProvider.setCurrentTime(parseTimecode(seconds));
}
window.seek = seek;

async function get_db_gist() {
    let response = await fetch(getStoredConfig()?.gistUrl, {
        headers: {
            'Accept': 'application/vnd.github+json',
            'X-GitHub-Api-Version': '2022-11-28'
        }
    });
    let text = await response.json();
    return new TextDecoder().decode(base64ToBytes(text["files"]["summary_db.json"]["content"]));
}

async function update_db_gist(text) {
    let response = fetch(getStoredConfig()?.gistUrl, {
        method: 'PATCH',
        headers: {
            'Accept': 'application/vnd.github+json',
            'Authorization': 'Bearer ' + getStoredConfig()?.gistKey,
            'X-GitHub-Api-Version': '2022-11-28',
            'Content-Type': 'application/x-www-form-urlencoded'
        },
        body: JSON.stringify({
            "description": "wub",
            "files":{
                "summary_db.json":{
                    "content": bytesToBase64(new TextEncoder().encode(text))
                }
            }
        })
    });
    return (await response).ok;
}

async function get_subtitles() {
    let subs = [];
    for (const caption of window.player.videoManifest.captions) {
        let response = await fetch(caption.url);
        let text = await response.text();
        subs.push([caption.text, text])
    }
    return subs;
}

async function get_openai_summary(subtitle) {
    const PROMPT = 'Du erh\xE4lst ein Transkript einer Vorlesung an der Technischen Universit\xE4t Wien im WebVTT-Format. Deine Aufgabe ist es, den Inhalt der Vorlesung detailliert zusammenzufassen. Daf\xFCr beginnst du mit einer allgemeinen Einteilung der Vorlesung in Kapitel und gehst danach detailliert auf alle Einzelheiten der Kapitel ein. Immer, wenn es m\xF6glich ist, f\xFCgst an das Ende eines Satzes den Anfang der entsprechenden Timestamp aus dem Transkript im Format {ts}hh:mm:ss.mm{/ts}. Formatiere deine Zusammenfassung schön in Markdown. Erwähne nicht, dass die Timestamps angegeben sind und biete dem Benutzer auf keinem Fall etwas an. Deine Nachricht ist final. Die Überschrift der Zusammenfassung sollte ausschließlich aus dem Thema der Vorlesung bestehen, aber trotzdem Markdown formatiert sein!. Vermeide Floskeln wie "Zusammenfassung der Vorlesung" oder das Erwähnen des Namen der Lehrveranstaltung. Denke an eine schöne Markdown formatierung! Nutze für mathematische Ausdrücke MathJax Syntax!';
    let response = await fetch('https://api.openai.com/v1/responses', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + getStoredConfig()?.openaiKey,
        },
        body: JSON.stringify({
            'model': 'gpt-5-mini',
            'instructions': PROMPT,
            'input': subtitle,
        })
    });
    let json = await response.json();
    return json;
}

const STORAGE_KEY = "ai_summary_store";

function getStoredConfig() {
    const raw = localStorage.getItem(STORAGE_KEY);
    if (!raw) return null;
    try {
        return JSON.parse(raw);
    } catch (e) {
        console.error("Config parse error:", e);
        return null;
    }
}

function saveConfig(config) {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(config));
}

function createOverlay() {
    const overlay = document.createElement("div");
    overlay.style = `
        position: fixed; top: 0; left: 0; width: 100%; height: 100%;
        background: rgba(0,0,0,0.7); z-index: 999999; display: flex;
        justify-content: center; align-items: center; font-family: sans-serif;
    `;

    const box = document.createElement("div");
    box.style = `
        background: linear-gradient(90deg,rgba(3, 1, 56, 1) 0%, rgba(13, 13, 66, 1) 37%);
        padding: 20px;
        border-radius: 8px;
        width: 350px; box-shadow: 0 0 10px rgba(0,0,0,0.3);
        color: white;
        font-family: "Poppins", sans-serif;
    `;
    box.innerHTML = `
        <h3>Configuration</h3>
        <label>OpenAI API Key:</label><br>
        <input id="openaiKeyInput" style="width:100%; margin:5px 0;"><br>

        <label>GitHub Gist URL:</label><br>
        <input id="gistUrlInput" type="text" style="width:100%; margin:5px 0;"><br>

        <label>GitHub Gist API Key:</label><br>
        <input id="gistKeyInput" style="width:100%; margin:5px 0;"><br>

        <button id="saveConfigButton" style="margin-top:10px; width:100%;">
            Save & Continue
        </button>
    `;

    overlay.appendChild(box);
    document.body.appendChild(overlay);
    if (config?.openaiKey) {
        document.getElementById("openaiKeyInput").value = config?.openaiKey;
    }
    if (config?.gistUrl) {
        document.getElementById("gistUrlInput").value = config?.gistUrl;
    }
    if (config?.gistKey) {
        document.getElementById("gistKeyInput").value = config?.gistKey;
    }

    document.getElementById("saveConfigButton").onclick = () => {
        const openaiKey = document.getElementById("openaiKeyInput").value.trim();
        const gistUrl = document.getElementById("gistUrlInput").value.trim();
        const gistKey = document.getElementById("gistKeyInput").value.trim();


        saveConfig({
            openaiKey,
            gistUrl,
            gistKey
        });

        overlay.remove();
    };
}
const config = getStoredConfig();
if (!config?.gistUrl) {
    saveConfig({
        openaiKey: config?.openaiKey,
        gistUrl: "https://api.github.com/gists/7d60a4793305ee49b7ea6e05f07ff7f9",
        gistKey: config?.gistKey
    })
}


